var utils = require('./utils')
var cssLoaders = utils.cssLoaders({
  sourceMap: process.PROJECT_CONFIG[process.env.NODE_ENV].sourceMap,
  extract: process.env.NODE_ENV === 'production'
})

module.exports = {
  loaders: Object.assign(
    {},
    cssLoaders,
    {
      js: 'happypack/loader?id=js&verbose=false'
    },
    { ts: 'happypack/loader?id=ts&verbose=false' }
  ),
  transformToRequire: {
    video: 'src',
    source: 'src',
    img: 'src',
    image: 'xlink:href'
  }
}
